mapper.select:
SELECT * FROM commercial_user

    AND id = #{id}
    AND title like CONCAT('%', #{title}, '%')
    AND user_name = #{userName}
    AND status = #{status}

ORDER BY sorts DESC limit #{stat},#{end}

SELECT COUNT(*) AS sumCount FROM commercial_user

    AND id = #{id}
    AND title like CONCAT('%', #{title}, '%')
    AND user_name = #{userName}
    AND status = #{status}

mapper.insert:
INSERT INTO commercial_user (title, user_name, api_key
                         , notify_url,ip_list,passage, remake, time, sorts)
VALUES (#{title},#{userName},#{apiKey},#{notifyUrl},#{ipList},#{passage},#{remake},now(),#{sorts});

mapper.update:
UPDATE commercial_user
title = #{title}, user_name = #{userName}, api_key = #{apiKey}, status = #{status}, notify_url = #{notifyUrl}, ip_list = #{ipList}, passage = #{passage}, remake = #{remake}, sorts = #{sorts},
WHERE id = #{id}
