mapper.select:
SELECT * FROM cashier_user

    AND id = #{cashierId}
    AND title like CONCAT('%', #{title}, '%')
    AND user_name = #{userName}
    AND status = #{status}

ORDER BY sorts DESC limit #{stat},#{end}

SELECT COUNT(*) AS sumCount FROM cashier_user

    AND id = #{cashierId}
    AND title like CONCAT('%', #{title}, '%')
    AND user_name = #{userName}
    AND status = #{status}

mapper.insert:
INSERT INTO cashier_user (title, user_name,type, api_key
                          , notify_url,lv, ip_list, remake, time, sorts)
VALUES (#{title},#{userName},#{type},#{apiKey},#{notifyUrl},#{lv},#{ipList},#{remake},now(),#{sorts});

mapper.update:
UPDATE cashier_user
title = #{title}, user_name = #{userName}, type = #{type}, api_key = #{apiKey}, status = #{status}, notify_url = #{notifyUrl}, withdraw_money = withdraw_money + #{withdrawMoney}, lv = #{lv}, ip_list = #{ipList}, remake = #{remake}, sorts = #{sorts},
WHERE id = #{id}

mapper.select:
SELECT * FROM cashier_user_check

    id <= (SELECT id FROM cashier_user_check

        AND cashier_id = #{cashierId}
        AND money = #{money}
        AND type = #{type}

            AND time BETWEEN #{startTime} AND #{endTime}


    ORDER BY id DESC LIMIT #{stat},1)
    AND cashier_id = #{cashierId}
    AND money = #{money}
    AND type = #{type}

        AND time BETWEEN #{startTime} AND #{endTime}


ORDER BY id DESC LIMIT #{end}

SELECT COUNT(*) AS sumCount FROM cashier_user_check

    AND cashier_id = #{cashierId}
    AND money = #{money}
    AND type = #{type}

        AND time BETWEEN #{startTime} AND #{endTime}
