mapper.select:
SELECT * FROM link_supplier_user

    AND title like CONCAT('%', #{title}, '%')
    AND type = #{type}
    AND zf_type = #{zfType}
    AND status = #{status}

ORDER BY sorts DESC limit #{stat},#{end}

SELECT COUNT(*) AS sumCount FROM link_supplier_user

    AND title like CONCAT('%', #{title}, '%')
    AND type = #{type}
    AND zf_type = #{zfType}
    AND status = #{status}

mapper.insert:
INSERT INTO link_supplier_user (title, user_name,type
                         , zf_type,phone_type,lv,url, remake, time, sorts)
VALUES (#{title},#{userName},#{type},#{zfType},#{phoneType},#{lv},#{url},#{remake},now(),#{sorts});

mapper.update:
UPDATE link_supplier_user
title = #{title}, user_name = #{userName}, type = #{type}, zf_type = #{zfType}, phone_type = #{phoneType}, status = #{status}, url = #{url}, lv = #{lv}, remake = #{remake}, sorts = #{sorts},
WHERE id = #{id}
